foreach (var plugin in PluginManager.Plugins.Where(p => p.Plugin.Name == "SideStep" || p.Plugin.Name == ""))
{
  try
  {
    if (!plugin.Enabled)
    {
      plugin.Enabled = true;
    }
    else
    {
      plugin.Enabled = false;
    }
  }
  catch (Exception ex)
  {

  }
}


